import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './Home.css';

const Home = () => {
  const [events, setEvents] = useState([]);
  const [filteredEvents, setFilteredEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('all');

  const categories = [
    { id: 'all', name: 'All Events', icon: 'üéØ' },
    { id: 'concert', name: 'Concerts', icon: 'üéµ' },
    { id: 'conference', name: 'Conferences', icon: 'üíº' },
    { id: 'sports', name: 'Sports', icon: '‚öΩ' },
    { id: 'workshop', name: 'Workshops', icon: 'üõ†Ô∏è' },
    { id: 'festival', name: 'Festivals', icon: 'üéâ' },
  ];

  useEffect(() => {
    fetchEvents();
  }, []);

  const fetchEvents = async () => {
    try {
      const response = await axios.get('http://localhost:8000/api/events/');
      setEvents(response.data);
      setFilteredEvents(response.data);
    } catch (error) {
      console.error('Error fetching events:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    filterEvents();
  }, [searchQuery, selectedCategory, events]);

  const filterEvents = () => {
    let filtered = events;

    if (searchQuery) {
      filtered = filtered.filter(event =>
        event.title?.toLowerCase().includes(searchQuery.toLowerCase()) ||
        event.description?.toLowerCase().includes(searchQuery.toLowerCase())
      );
    }

    setFilteredEvents(filtered);
  };

  return (
    <div className="home">
      {/* Hero Section */}
      <section className="hero">
        <div className="hero-content">
          <h1 className="hero-title">
            Discover Amazing<br />
            <span className="gradient-text">Events Happening Now</span>
          </h1>
          <p className="hero-subtitle">
            Find and book tickets to the most exciting events in your college
          </p>
          <div className="hero-search">
            <input
              type="text"
              placeholder="Search events, artists, venues..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="search-input"
            />
            <button className="btn btn-primary">Search</button>
          </div>
        </div>
        <div className="hero-visual">
          <div className="floating-card card-1">üé´</div>
          <div className="floating-card card-2">üéµ</div>
          <div className="floating-card card-3">üé¨</div>
        </div>
      </section>

      {/* Categories */}
      <section className="categories-section">
        <div className="container">
          <h2>Browse by Category</h2>
          <div className="categories">
            {categories.map(category => (
              <button
                key={category.id}
                className={`category-btn ${selectedCategory === category.id ? 'active' : ''}`}
                onClick={() => setSelectedCategory(category.id)}
              >
                <span className="category-icon">{category.icon}</span>
                <span className="category-name">{category.name}</span>
              </button>
            ))}
          </div>
        </div>
      </section>

      {/* Events Grid */}
      <section className="events-section">
        <div className="container">
          <div className="section-header">
            <h2>Upcoming Events</h2>
            <p className="section-subtitle">
              {filteredEvents.length} events available
            </p>
          </div>

          {loading ? (
            <div className="loading-grid">
              {[1, 2, 3, 4, 5, 6].map(i => (
                <div key={i} className="event-card skeleton"></div>
              ))}
            </div>
          ) : filteredEvents.length > 0 ? (
            <div className="events-grid grid-3">
              {filteredEvents.map(event => (
                <Link
                  key={event.id}
                  to={`/events/${event.id}`}
                  className="event-card"
                >
                  <div className="event-image">
                    <img
                      src={event.image_url || 'https://via.placeholder.com/300x200?text=Event'}
                      alt={event.title}
                    />
                    <div className="event-badge">{event.category || 'Event'}</div>
                  </div>
                  <div className="event-body">
                    <h3 className="event-title">{event.title}</h3>
                    <p className="event-description">
                      {event.description?.substring(0, 60)}...
                    </p>
                    <div className="event-meta">
                      <span className="meta-item">
                        üìÖ {new Date(event.date).toLocaleDateString()}
                      </span>
                      <span className="meta-item">
                        üìç {event.location || 'TBD'}
                      </span>
                    </div>
                    <div className="event-footer">
                      <span className="event-price">
                        ‚Çπ{event.price || 'Free'}
                      </span>
                      <span className="event-status">
                        {event.available_seats > 0 ? (
                          <span className="badge-available">Available</span>
                        ) : (
                          <span className="badge-sold">Sold Out</span>
                        )}
                      </span>
                    </div>
                  </div>
                </Link>
              ))}
            </div>
          ) : (
            <div className="empty-state">
              <span className="empty-icon">üîç</span>
              <h3>No events found</h3>
              <p>Try adjusting your search or filters</p>
            </div>
          )}
        </div>
      </section>
    </div>
  );
};

export default Home;
