import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Dashboard.css';

const Dashboard = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [events, setEvents] = useState([]);
  const [registrations, setRegistrations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('overview');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          navigate('/login');
          return;
        }

        // Fetch user profile
        const userResponse = await axios.get('http://localhost:8000/api/users/profile/', {
          headers: { Authorization: `Token ${token}` }
        });
        setUser(userResponse.data);

        // Fetch events
        const eventsResponse = await axios.get('http://localhost:8000/api/events/', {
          headers: { Authorization: `Token ${token}` }
        });
        setEvents(eventsResponse.data);

        // Fetch user registrations
        const registrationsResponse = await axios.get('http://localhost:8000/api/registrations/registrations/', {
          headers: { Authorization: `Token ${token}` }
        });
        setRegistrations(registrationsResponse.data);

        setLoading(false);
      } catch (error) {
        console.error('Error fetching data:', error);
        setLoading(false);
      }
    };

    fetchData();
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  const upcomingEvents = events.filter(event => new Date(event.date) > new Date());
  const recentEvents = events.slice(0, 6);
  const userRegistrations = registrations.length;

  if (loading) {
    return (
      <div className="dashboard-loading">
        <div className="loading-spinner"></div>
        <p>Loading your dashboard...</p>
      </div>
    );
  }

  return (
    <div className="dashboard">
      {/* Navigation Header */}
      <nav className="dashboard-nav">
        <div className="nav-brand">
          <h1>EventHub</h1>
        </div>
        <div className="nav-tabs">
          <button
            className={`nav-tab ${activeTab === 'overview' ? 'active' : ''}`}
            onClick={() => setActiveTab('overview')}
          >
            Overview
          </button>
          <button
            className={`nav-tab ${activeTab === 'events' ? 'active' : ''}`}
            onClick={() => setActiveTab('events')}
          >
            My Events
          </button>
          <button
            className={`nav-tab ${activeTab === 'profile' ? 'active' : ''}`}
            onClick={() => setActiveTab('profile')}
          >
            Profile
          </button>
        </div>
        <div className="nav-user">
          <span className="welcome-text">Welcome, {user?.name || 'User'}</span>
          <button onClick={handleLogout} className="logout-btn">Logout</button>
        </div>
      </nav>

      <main className="dashboard-main">
        {activeTab === 'overview' && (
          <>
            {/* Hero Welcome Section */}
            <section className="welcome-section">
              <div className="welcome-content">
                <h2>Your Event Dashboard</h2>
                <p>Discover, register, and manage your college events all in one place.</p>
                <div className="quick-actions">
                  <button className="btn-primary" onClick={() => navigate('/events')}>
                    Browse Events
                  </button>
                  <button className="btn-secondary" onClick={() => setActiveTab('events')}>
                    My Registrations
                  </button>
                </div>
              </div>
              <div className="welcome-image">
                <span>üéì</span>
              </div>
            </section>

            {/* Stats Cards */}
            <section className="stats-section">
              <div className="stats-grid">
                <div className="stat-card">
                  <div className="stat-icon">üìÖ</div>
                  <div className="stat-content">
                    <h3>{events.length}</h3>
                    <p>Total Events</p>
                  </div>
                </div>
                <div className="stat-card">
                  <div className="stat-icon">‚úÖ</div>
                  <div className="stat-content">
                    <h3>{userRegistrations}</h3>
                    <p>My Registrations</p>
                  </div>
                </div>
                <div className="stat-card">
                  <div className="stat-icon">‚è∞</div>
                  <div className="stat-content">
                    <h3>{upcomingEvents.length}</h3>
                    <p>Upcoming Events</p>
                  </div>
                </div>
                <div className="stat-card">
                  <div className="stat-icon">üèÜ</div>
                  <div className="stat-content">
                    <h3>{user?.role === 'organizer' ? 'Organizer' : 'Student'}</h3>
                    <p>Your Role</p>
                  </div>
                </div>
              </div>
            </section>

            {/* Recent Events Carousel */}
            <section className="recent-events-section">
              <h2>Recent Events</h2>
              <div className="events-carousel">
                {recentEvents.map(event => (
                  <div key={event.id} className="event-card">
                    <div className="event-image">
                      <span>üìÖ</span>
                    </div>
                    <div className="event-content">
                      <h3>{event.title}</h3>
                      <p>{event.description.substring(0, 80)}...</p>
                      <div className="event-meta">
                        <span className="event-date">üìÖ {new Date(event.date).toLocaleDateString()}</span>
                        <span className="event-status">{event.status}</span>
                      </div>
                      <button className="btn-primary" onClick={() => navigate('/events')}>
                        View Details
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            </section>
          </>
        )}

        {activeTab === 'events' && (
          <section className="my-events-section">
            <h2>My Events</h2>
            {userRegistrations > 0 ? (
              <div className="events-grid">
                {registrations.map(reg => {
                  const event = events.find(e => e.id === reg.event);
                  return event ? (
                    <div key={reg.id} className="event-card registered">
                      <div className="event-image">
                        <span>‚úÖ</span>
                      </div>
                      <div className="event-content">
                        <h3>{event.title}</h3>
                        <p>{event.description.substring(0, 100)}...</p>
                        <div className="event-meta">
                          <span className="event-date">üìÖ {new Date(event.date).toLocaleDateString()}</span>
                          <span className="registration-status">Registered</span>
                        </div>
                      </div>
                    </div>
                  ) : null;
                })}
              </div>
            ) : (
              <div className="no-events">
                <span>üì≠</span>
                <h3>No registrations yet</h3>
                <p>Start exploring events and register for the ones that interest you!</p>
                <button className="btn-primary" onClick={() => navigate('/events')}>
                  Browse Events
                </button>
              </div>
            )}
          </section>
        )}

        {activeTab === 'profile' && (
          <section className="profile-section">
            <h2>My Profile</h2>
            <div className="profile-card">
              <div className="profile-avatar">
                <span>üë§</span>
              </div>
              <div className="profile-info">
                <h3>{user?.name || 'User'}</h3>
                <p><strong>Username:</strong> {user?.username}</p>
                <p><strong>Email:</strong> {user?.email}</p>
                <p><strong>Role:</strong> {user?.role}</p>
                <p><strong>Department:</strong> {user?.department || 'Not specified'}</p>
                <p><strong>Joined:</strong> {user?.date_joined ? new Date(user.date_joined).toLocaleDateString() : 'Unknown'}</p>
              </div>
            </div>
          </section>
        )}
      </main>
    </div>
  );
};

export default Dashboard;
