import React, { useState, useEffect, useCallback, useMemo } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Login.css';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [events, setEvents] = useState([]);
  const [showLogin, setShowLogin] = useState(false);
  const [currentSlide, setCurrentSlide] = useState(0);
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  // Memoize featured events to prevent unnecessary recalculations
  const featuredEvents = useMemo(() => events.slice(0, 6), [events]);

  // Memoize total slides calculation
  const totalSlides = useMemo(() => Math.ceil(featuredEvents.length / 3), [featuredEvents.length]);

  // Optimized fetch with loading state
  const fetchEvents = useCallback(async () => {
    try {
      const response = await axios.get('http://localhost:8000/api/events/');
      setEvents(Array.isArray(response.data) ? response.data : []);
    } catch (err) {
      console.error('Error fetching events:', err);
      setEvents([]);
    }
  }, []);

  useEffect(() => {
    fetchEvents();
  }, [fetchEvents]);

  // Optimized carousel timer with cleanup
  useEffect(() => {
    if (totalSlides <= 1) return;

    const timer = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % totalSlides);
    }, 5000);

    return () => clearInterval(timer);
  }, [totalSlides]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:8000/api/auth/login/', {
        username,
        password,
      });
      localStorage.setItem('token', response.data.token);
      const role = response.data.role;
      if (role === 'admin') {
        navigate('/admin/dashboard');
      } else {
        navigate('/dashboard');
      }
    } catch (err) {
      setError('Invalid credentials');
    }
  };

  const handleRegister = (eventId) => {
    if (!localStorage.getItem('token')) {
      setShowLogin(true);
    } else {
      navigate('/events');
    }
  };

  return (
    <div className="homepage">
      {/* Hero Section */}
      <section className="hero">
        <div className="hero-content">
          <h1 className="hero-title">Discover Amazing College Events</h1>
          <p className="hero-subtitle">Connect, learn, and grow with your college community</p>
          <div className="hero-actions">
            <button className="btn-primary" onClick={() => navigate('/events')}>
              Explore Events
            </button>
            <button className="btn-secondary" onClick={() => setShowLogin(true)}>
              Login to Register
            </button>
          </div>
        </div>
        <div className="hero-image">
          <div className="hero-placeholder">
            <span>ðŸŽ“</span>
          </div>
        </div>
      </section>

      {/* Featured Events Carousel */}
      <section className="featured-events">
        <div className="container">
          <h2 className="section-title">Featured Events</h2>
          {featuredEvents.length > 0 ? (
            <div className="carousel">
              <div className="carousel-track" style={{ transform: `translateX(-${currentSlide * 100}%)` }}>
                {Array.from({ length: Math.ceil(featuredEvents.length / 3) }, (_, slideIndex) => (
                  <div key={slideIndex} className="carousel-slide">
                    {featuredEvents.slice(slideIndex * 3, (slideIndex + 1) * 3).map(event => (
                      <div key={event.id} className="event-card">
                        <div className="event-image">
                          <span>ðŸ“…</span>
                        </div>
                        <div className="event-content">
                          <h3>{event.title}</h3>
                          <p>{event.description.substring(0, 100)}...</p>
                          <p className="event-date">ðŸ“… {new Date(event.date).toLocaleDateString()}</p>
                          <button className="btn-primary" onClick={() => handleRegister(event.id)}>
                            Register Now
                          </button>
                        </div>
                      </div>
                    ))}
                  </div>
                ))}
              </div>
              <div className="carousel-indicators">
                {Array.from({ length: Math.ceil(featuredEvents.length / 3) }, (_, index) => (
                  <button
                    key={index}
                    className={`indicator ${index === currentSlide ? 'active' : ''}`}
                    onClick={() => setCurrentSlide(index)}
                  />
                ))}
              </div>
            </div>
          ) : (
            <div className="no-events">
              <span>ðŸŽ­</span>
              <p>No events available at the moment. Check back soon!</p>
            </div>
          )}
        </div>
      </section>

      {/* Stats Section */}
      <section className="stats-section">
        <div className="container">
          <div className="stats-grid">
            <div className="stat-item">
              <div className="stat-number">{events.length}</div>
              <div className="stat-label">Total Events</div>
            </div>
            <div className="stat-item">
              <div className="stat-number">500+</div>
              <div className="stat-label">Students</div>
            </div>
            <div className="stat-item">
              <div className="stat-number">50+</div>
              <div className="stat-label">Organizers</div>
            </div>
            <div className="stat-item">
              <div className="stat-number">1000+</div>
              <div className="stat-label">Registrations</div>
            </div>
          </div>
        </div>
      </section>

      {/* Login Modal */}
      {showLogin && (
        <div className="login-modal">
          <div className="modal-content">
            <h2>Login to Your Account</h2>
            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label>Username:</label>
                <input
                  type="text"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  required
                  placeholder="Enter your username"
                />
              </div>
              <div className="form-group">
                <label>Password:</label>
                <input
                  type="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                  placeholder="Enter your password"
                />
              </div>
              {error && <p className="error">{error}</p>}
              <div className="modal-actions">
                <button type="submit" className="btn-primary">Login</button>
                <button type="button" className="btn-secondary" onClick={() => setShowLogin(false)}>Cancel</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Login;
