import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Dashboard.css';

const Dashboard = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch user data and events
    const fetchData = async () => {
      try {
        // Assuming token is stored in localStorage
        const token = localStorage.getItem('token');
        if (!token) {
          navigate('/login');
          return;
        }

        // Fetch user profile
        const userResponse = await axios.get('http://localhost:8000/api/users/profile/', {
          headers: { Authorization: `Token ${token}` }
        });
        setUser(userResponse.data);

        // Fetch events
        const eventsResponse = await axios.get('http://localhost:8000/api/events/', {
          headers: { Authorization: `Token ${token}` }
        });
        setEvents(eventsResponse.data);

        setLoading(false);
      } catch (error) {
        console.error('Error fetching data:', error);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    window.location.href = '/login';
  };

  if (loading) {
    return <div className="dashboard-loading">Loading...</div>;
  }

  return (
    <div className="dashboard">
      <header className="dashboard-header">
        <h1>College Event Management Dashboard</h1>
        <div className="user-info">
          <span>Welcome, {user?.name || 'User'}</span>
          <button onClick={handleLogout} className="logout-btn">Logout</button>
        </div>
      </header>

      <main className="dashboard-main">
        <div className="dashboard-stats">
          <div className="stat-card">
            <h3>Total Events</h3>
            <p>{events.length}</p>
          </div>
          <div className="stat-card">
            <h3>My Registrations</h3>
            <p>0</p> {/* TODO: Fetch actual registrations */}
          </div>
          <div className="stat-card">
            <h3>Upcoming Events</h3>
            <p>{events.filter(event => new Date(event.date) > new Date()).length}</p>
          </div>
        </div>

        <div className="dashboard-actions">
          <button onClick={() => navigate('/events')} className="action-btn">
            View All Events
          </button>
          {/* Add more actions based on user role */}
        </div>

        <div className="recent-events">
          <h2>Recent Events</h2>
          <div className="events-list">
            {events.slice(0, 5).map(event => (
              <div key={event.id} className="event-card">
                <h3>{event.title}</h3>
                <p>{event.description}</p>
                <p>Date: {new Date(event.date).toLocaleDateString()}</p>
              </div>
            ))}
          </div>
        </div>
      </main>
    </div>
  );
};

export default Dashboard;
