import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './AdminDashboard.css';

const AdminDashboard = () => {
  const [activeTab, setActiveTab] = useState('overview');
  const [dashboardData, setDashboardData] = useState({});
  const [users, setUsers] = useState([]);
  const [events, setEvents] = useState([]);
  const [reports, setReports] = useState({});
  const [loading, setLoading] = useState(true);
  const [userFilters, setUserFilters] = useState({ role: '', department: '' });
  const [eventFilters, setEventFilters] = useState({ status: '', organizer: '' });

  useEffect(() => {
    fetchDashboardData();
    fetchUsers();
    fetchEvents();
    fetchReports();
  }, []);

  const fetchDashboardData = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:8000/api/admin/dashboard/', {
        headers: { Authorization: `Token ${token}` }
      });
      setDashboardData(response.data);
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    }
  };

  const fetchUsers = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:8000/api/admin/users/', {
        headers: { Authorization: `Token ${token}` }
      });
      setUsers(response.data);
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };

  const fetchEvents = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:8000/api/admin/events/', {
        headers: { Authorization: `Token ${token}` }
      });
      setEvents(response.data);
    } catch (error) {
      console.error('Error fetching events:', error);
    }
  };

  const fetchReports = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:8000/api/reports/', {
        headers: { Authorization: `Token ${token}` }
      });
      setReports(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching reports:', error);
      setLoading(false);
    }
  };

  const handleDeleteUser = async (id) => {
    if (window.confirm('Are you sure you want to delete this user?')) {
      try {
        const token = localStorage.getItem('token');
        await axios.delete(`http://localhost:8000/api/admin/users/${id}/`, {
          headers: { Authorization: `Token ${token}` }
        });
        setUsers(users.filter(user => user.id !== id));
      } catch (error) {
        console.error('Error deleting user:', error);
      }
    }
  };

  const handleDeleteEvent = async (id) => {
    if (window.confirm('Are you sure you want to delete this event?')) {
      try {
        const token = localStorage.getItem('token');
        await axios.delete(`http://localhost:8000/api/admin/events/${id}/`, {
          headers: { Authorization: `Token ${token}` }
        });
        setEvents(events.filter(event => event.id !== id));
      } catch (error) {
        console.error('Error deleting event:', error);
      }
    }
  };

  const handleApproveEvent = async (id) => {
    try {
      const token = localStorage.getItem('token');
      await axios.patch(`http://localhost:8000/api/admin/events/${id}/`, { status: 'approved' }, {
        headers: { Authorization: `Token ${token}` }
      });
      setEvents(events.map(event => event.id === id ? { ...event, status: 'approved' } : event));
    } catch (error) {
      console.error('Error approving event:', error);
    }
  };

  const handleRejectEvent = async (id) => {
    try {
      const token = localStorage.getItem('token');
      await axios.patch(`http://localhost:8000/api/admin/events/${id}/`, { status: 'rejected' }, {
        headers: { Authorization: `Token ${token}` }
      });
      setEvents(events.map(event => event.id === id ? { ...event, status: 'rejected' } : event));
    } catch (error) {
      console.error('Error rejecting event:', error);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    window.location.href = '/login';
  };

  const renderDashboard = () => (
    <div className="dashboard-content">
      <h2>Dashboard</h2>
      <div className="big-numbers">
        <div className="number-card">
          <h3>Total Users</h3>
          <p>{dashboardData.big_numbers?.total_users || 0}</p>
        </div>
        <div className="number-card">
          <h3>Total Events</h3>
          <p>{dashboardData.big_numbers?.total_events || 0}</p>
        </div>
        <div className="number-card">
          <h3>Total Registrations</h3>
          <p>{dashboardData.big_numbers?.total_registrations || 0}</p>
        </div>
        <div className="number-card">
          <h3>Total Attendance</h3>
          <p>{dashboardData.big_numbers?.total_attendance || 0}</p>
        </div>
      </div>
      <div className="alerts">
        <h3>Alerts</h3>
        <ul>
          {dashboardData.alerts?.map((alert, index) => (
            <li key={index}>{alert}</li>
          ))}
        </ul>
      </div>
      <div className="recent-activity">
        <h3>Recent Activity</h3>
        <p>Registrations last 7 days: {dashboardData.recent_activity?.registrations_last_7_days || 0}</p>
        <p>Attendance last 7 days: {dashboardData.recent_activity?.attendance_last_7_days || 0}</p>
      </div>
      <div className="user-breakdown">
        <h3>User Breakdown</h3>
        <p>Students: {dashboardData.user_breakdown?.students || 0}</p>
        <p>Organizers: {dashboardData.user_breakdown?.organizers || 0}</p>
        <p>Admins: {dashboardData.user_breakdown?.admins || 0}</p>
      </div>
      <div className="recent-events">
        <h3>Recent Events</h3>
        <ul>
          {dashboardData.recent_events?.map(event => (
            <li key={event.id}>{event.title} - {event.status}</li>
          ))}
        </ul>
      </div>
    </div>
  );

  const filteredUsers = users.filter(user =>
    (userFilters.role === '' || user.role === userFilters.role) &&
    (userFilters.department === '' || user.department === userFilters.department)
  );

  const renderUsers = () => (
    <div className="users-content">
      <h2>Manage Users</h2>
      <div className="filters">
        <select value={userFilters.role} onChange={(e) => setUserFilters({ ...userFilters, role: e.target.value })}>
          <option value="">All Roles</option>
          <option value="student">Student</option>
          <option value="organizer">Organizer</option>
          <option value="admin">Admin</option>
        </select>
        <input
          type="text"
          placeholder="Filter by Department"
          value={userFilters.department}
          onChange={(e) => setUserFilters({ ...userFilters, department: e.target.value })}
        />
      </div>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Username</th>
            <th>Email</th>
            <th>Role</th>
            <th>Department</th>
            <th>Date Joined</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {filteredUsers.map(user => (
            <tr key={user.id}>
              <td>{user.id}</td>
              <td>{user.username}</td>
              <td>{user.email}</td>
              <td>{user.role}</td>
              <td>{user.department}</td>
              <td>{user.date_joined}</td>
              <td>
                <button onClick={() => handleDeleteUser(user.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );

  const filteredEvents = events.filter(event =>
    (eventFilters.status === '' || event.status === eventFilters.status) &&
    (eventFilters.organizer === '' || event.organizer__username.toLowerCase().includes(eventFilters.organizer.toLowerCase()))
  );

  const renderEvents = () => (
    <div className="events-content">
      <h2>Manage Events</h2>
      <div className="filters">
        <select value={eventFilters.status} onChange={(e) => setEventFilters({ ...eventFilters, status: e.target.value })}>
          <option value="">All Statuses</option>
          <option value="pending">Pending</option>
          <option value="approved">Approved</option>
          <option value="rejected">Rejected</option>
        </select>
        <input
          type="text"
          placeholder="Filter by Organizer"
          value={eventFilters.organizer}
          onChange={(e) => setEventFilters({ ...eventFilters, organizer: e.target.value })}
        />
      </div>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Title</th>
            <th>Description</th>
            <th>Date</th>
            <th>Status</th>
            <th>Organizer</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {filteredEvents.map(event => (
            <tr key={event.id}>
              <td>{event.id}</td>
              <td>{event.title}</td>
              <td>{event.description}</td>
              <td>{event.date}</td>
              <td>{event.status}</td>
              <td>{event.organizer__username}</td>
              <td>
                {event.status === 'pending' && (
                  <>
                    <button onClick={() => handleApproveEvent(event.id)}>Approve</button>
                    <button onClick={() => handleRejectEvent(event.id)}>Reject</button>
                  </>
                )}
                <button onClick={() => handleDeleteEvent(event.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );

  const renderReports = () => (
    <div className="reports-content">
      <h2>Reports</h2>
      <p>{reports.message || 'Reports will be displayed here.'}</p>
    </div>
  );

  const renderContent = () => {
    switch (activeTab) {
      case 'dashboard':
        return renderDashboard();
      case 'users':
        return renderUsers();
      case 'events':
        return renderEvents();
      case 'reports':
        return renderReports();
      default:
        return renderDashboard();
    }
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div className="admin-dashboard">
      <div className="sidebar">
        <h2>Admin Panel</h2>
        <ul>
          <li className={activeTab === 'dashboard' ? 'active' : ''} onClick={() => setActiveTab('dashboard')}>Dashboard</li>
          <li className={activeTab === 'users' ? 'active' : ''} onClick={() => setActiveTab('users')}>Users</li>
          <li className={activeTab === 'events' ? 'active' : ''} onClick={() => setActiveTab('events')}>Events</li>
          <li className={activeTab === 'reports' ? 'active' : ''} onClick={() => setActiveTab('reports')}>Reports</li>
        </ul>
      </div>
      <div className="main-content">
        {renderContent()}
      </div>
    </div>
  );
};

export default AdminDashboard;
